package javaintro;

public class Ifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age=18;
		if(age>=18)
		{
			System.out.println("your eligible for  vote");
		}
		else
		{
			System.out.println("your not eligible for vote");
		}
		
		//if else condition
		int mark=80;
		if(mark>=100)
		{
			System.out.println("your Grade A:"+mark);
		}
		else if(mark>=90)
		{
			System.out.println("your Grade B:"+mark);
		}
		else if(mark>=80)
		{
			System.out.println("your Grade c:"+mark);
		}
		else
		{
			System.out.println("your Grade D:"+mark);
		}
		
	///////////////////////////////////////////////////////
		int a=10;
		int b=20;
		
		//conditional AND
		System.out.println(a>b&&a<b);
		System.out.println(a<b&&a<b);
		
		
		//conditional or
		System.out.println(a>b||a>b);
		System.out.println(a<b||a>b);
		
		
		
		
	
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
			

	}

}
