package javaintro;

public class Switch_case {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int name=2;
		switch(name)
		{
		case 1:System.out.println("Ranjith");
		break;
		case 2:System.out.println("Kailash");
		break;
		case 3:System.out.println("mithun");
		break;
		case 4:System.out.println("rajesh");
		break;
		default:
			System.out.println("invalid name");
		}
		
		
			
	
		
			

	}

}
