package javaintro;

public class While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//while loop
		int n=4;
		while(n>=1)
		{
			System.out.print(n);
			n--;
		}
		
		//do while
		int i=4;
		do {
			System.out.println(i);
			i++;
			}
		while(i<=7);


	}

}
